current.google_font_styles:
/* Main */
    header button .icon-f-39:before {
      content: "\e9ed";
    }
    header .tt-dropdown-inner .icon-f-39:before {
      content: "\e98e";
    }
    .tt-product .tt-description .tt-btn-addtocart .icon-shopping_basket:before {
      content: '\e9ed' !important;
    }
    header button .icon-f-94:before {
      content: "\ea43";
    }
    .icon-f-85:before {
      content: "\ea23";
    }

    #tt-pageContent .shopify-section > .container-indent:not(.nomargin):not([class^="tt-offset"]):not(.smaller-main-margin):not(.sec-title-padding) {
      margin-top: 72px;
    }
    .btn{
      letter-spacing: 0;
    }


    .tt-block-title {
      text-align: left;
      padding-bottom: 25px;
    }
    .tt-block-title .tt-description {
      font-size: 18px;
      line-height: 24px;
      letter-spacing: 0;
      font-weight: 400;
    }

    /* Header */
    .tt-desctop-menu .tt-badge {
      padding: 4px 4px 4px;
    }
    .header-tel-info i {
      color: inherit;
    }
    .categories-btn > button {
      font-size: 22px;
      letter-spacing: 0;
      padding: 13px 20px 16px 20px;
    }
    .tt-stuck-nav .categories-btn > button {
      padding: 9px 20px 11px 20px;
      top: 0;
    }
    .categories-btn > button i{
      font-size: 16px;
    }
    .categories-btn .icon-e-13:before {
      content: "\e9c0";
    }
    .categories-btn .icon-e-16:before {
      content: "\e9c3";
    }

    .tt-desctop-menu nav > ul > li.dropdown > a {
      font-size: 16px;
      letter-spacing: 0;
    }
    .headertype2.small-header .tt-desktop-header .tt-header-holder .tt-obj-menu .tt-desctop-menu.tt-menu-small nav > ul > li.dropdown > a {
      padding-top: 16px;
      padding-bottom: 17px;
    }

    header .tt-dropdown-obj .tt-dropdown-toggle {
      font-size: 22px;
    }

    .search-type2 button {
      font-weight: 400;
      font-size: 16px;
      padding: 9px 20px 9px;
    }
    .search-type2 input {
      font-weight: 300;
      font-size: 16px;
      padding: 8px 10px 8px 15px;
    }
    .search-type2 form i {
      display: none;
    }
    .search-type2 form{
      margin-left: 83px !important;
    }

    .headertype2 .tt-desktop-header > .container:first-child .tt-header-holder{
      padding: 11px 0 2px;
    }

    @media (max-width: 1024px){
      .header-tel-info {
        padding-top: 8px;
        padding-bottom: 8px;
      }
    }

    /* Product */
    .tt-product .tt-description .tt-btn-addtocart{
      font-size: 16px !important;
      letter-spacing: 0 !important;
    }
    .tt-product .tt-description .tt-btn-addtocart.thumbprod-button-bg span:first-child {
      top: 4px !important;
    }
    @media (min-width: 1025px){
      .tt-product .tt-description .tt-title {
        font-size: 16px;
      }
    }
    @media (min-width: 1025px){
      .tt-product .tt-description .tt-price {
        font-size: 18px;
        margin-bottom: 4px;
      }
    }
    /* Content */
    .slider-revolution .tp-bullets .bullet {
      background-color: #ffffff;
    }
    .slider-revolution .tp-caption1-wd-2 {
      font-family: 'Passion One', cursive;
      font-weight: 400;
    }
    .slider-revolution .fnt-we-500 {
      font-weight: 400;
    }
    .slider-revolution .btn{
      font-size: 16px;
      padding-bottom: 9px;
    }

    .tt-promo-box .tt-description .tt-title-small {
      font-size: 22px;
      margin-top: 6px;
    }
    .tt-promo-box .tt-description .tt-title-large {
      font-family: 'Passion One', cursive;
      font-weight: 400;
      font-size: 40px;
      line-height: 43px;
      margin-bottom: 7px;
    }

    .sec-title-padding .tt-block-title {
      padding-bottom: 10px;
    }
    .tt-promo02 {
      margin-top: 35px;
    }
    .tt-promo02 .tt-description .tt-title-large {
      font-size: 22px;
      line-height: 28px;
      letter-spacing: 0;
    }
    .tt-promo02 .tt-description .tt-title-large:hover {
      text-decoration: underline;
    }
    .tt-promo02 .image-box {
      border-radius: 6px;
    }

    .tt-tabs-default li > a {
      font-size: 18px;
      font-weight: 400;
      letter-spacing: 0; 
    }
    @media (min-width: 1025px){
      .tt-tabs-default {
        -webkit-justify-content: left;
        -ms-flex-pack: left;
        justify-content: left;
        padding-left: 0;
        padding-right: 0;
        margin-left: -14px;
        padding-bottom: 29px;
      }
    }
    .tt-collapse .tt-collapse-title {
      font-size: 18px;
      line-height: 26px;
      font-weight: 400;
      letter-spacing: 0;
    }
    /* Footer */
    footer{
      font-size: 16px;
      line-height: 28px;
      font-weight: 300;
    }
    footer .tt-mobile-collapse .tt-collapse-title {
      font-size: 18px;
    }
    footer .tt-box-copyright {
      font-size: 16px;
    }
    footer .tt-mobile-collapse .tt-collapse-title {
      letter-spacing: 0;
      font-weight: 400;
    }
    footer b{
      font-weight: 400;
      color: #191919;
    }
.hs-site-cart-popup-layout * {
    font-family: Mikado !important;
font-weight: 700 !important
}
